  PHP使用CURL上传文件到远程服务接口
当然你的php.ini开启了curl扩展功能
1、文件上传/** * 以文件上传 第二种方法 */ public function upload(){ $result = array(&#39;code&#39; =&gt; 0, &#39;message&#39; =&gt; &#39;ok&#39;); ini_set(&#39;upload_max_filesize&#39;, &#39;20M&#39;); ini_set(&#39;post_max_size&#39;, &#39;20M&#39;); ini_set(&#39;memory_limit&#39;, &#39;128M&#39;); $url=&#34;http://xx.xx.xx.xx/api/file/upload&#34;; //远程接口地址 $file = $_FILES[&#39;file&#39;]; if ($file[&#39;error&#39;] != 0) { $result[&#39;code&#39;] = 40001; $result[&#39;message&#39;] = &#39;上传出错&#39;; $this-&gt;error(); //使用Thinkphp5.1内置controller方法 } else { $filename = $file[&#39;name&#39;]; $tmpfile = $file[&#39;tmp_name&#39;]; $filetype = $file[&#39;type&#39;]; $data = self::upload_file($url, $filename, $tmpfile, $filetype); $data = json_decode($data,true); $this-&gt;success(); ////使用Thinkphp5.1内置controller方法 die; } } /** * curl上传文件 * * @param unknown $url * @param unknown $filename * @param unknown $path * @param unknown $type */ protected function upload_file($url,$filename,$path,$type){ //php 5.5以上的用法 if (class_exists(&#39;\CURLFile&#39;)) { $data = array(&#39;file&#39; =&gt; new \CURLFile(realpath($path),$type,$filename)); } else { $data = array( &#39;file&#39;=&gt;&#39;@&#39;.realpath($path).&#34;;type=&#34;.$type.&#34;;filename=&#34;.$filename ); } $ch = curl_init(); curl_setopt($ch, CURLOPT_URL, $url); curl_setopt($ch, CURLOPT_POST, true ); curl_setopt($ch, CURLOPT_POSTFIELDS, $data); curl_setopt($ch, CURLOPT_HEADER, false); curl_setopt($ch, CURLOPT_RETURNTRANSFER, true); $return_data = curl_exec($ch); curl_close($ch); echo $return_data; } 2、文件接收端（不限定于PHP)/** * 接收文件 */ public function upload(){ Log::info(&#39;--------------$_FILES--------------------&#39;); Log::info($_FILES); if( $_FILES){ $filename = $_FILES[&#39;file&#39;][&#39;name&#39;]; $tmpname = $_FILES[&#39;file&#39;][&#39;tmp_name&#39;]; if(move_uploaded_file($tmpname, $_SERVER[&#39;DOCUMENT_ROOT&#39;].&#34;/excel-file/&#34; .$filename)){ echo json_encode(&#39;上传成功&#39;); }else{ $data = json_encode($_FILES); echo $data; } } } 3、以下是以二进制文件上传偶尔会失败，故而不推荐使用，在此记录下！/** * 以二进制文件上传 */ public function upload_stream(){ $url=&#34;http://xx.xx.xx.xx/api/file/upload&#34;; //上传远程接口地址 $info = $_FILES[&#39;file&#39;]; $fp = fopen($info[&#39;tmp_name&#39;], &#39;r&#39;); $filebinary = fread($fp, filesize($info[&#39;tmp_name&#39;])); fclose($fp); $opts = array( &#39;http&#39; =&gt; array( &#39;method&#39; =&gt; &#39;POST&#39;, &#39;header&#39; =&gt; &#39;content-type:application/x-www-form-urlencoded&#39;, &#39;context&#39; =&gt; $filebinary ) ); $context = stream_context_create($opts); $response = file_get_contents($url, false, $context); //就是这个方法 偶尔会失败哦！ $ret = json_decode($response, true); return $ret[&#39;success&#39;]; } //以下服务部署于服务端(不限定于PHP) /** * 接收二进制流文件 */ public function upload_stream(){ $streamData = isset($GLOBALS[&#39;HTTP_RAW_POST_DATA&#39;]) ? $GLOBALS[&#39;HTTP_RAW_POST_DATA&#39;] : &#39;&#39;; $receiveFile = $_SERVER[&#39;DOCUMENT_ROOT&#39;].&#34;/excel-file/receiv.xlsx&#34;; if(empty($streamData)){ $streamData = file_get_contents(&#39;php://input&#39;); } if($streamData!=&#39;&#39;){ $ret = file_put_contents($receiveFile, $streamData, true); }else{ $ret = false; } return $ret; }
